CREATE TABLE places (id BIGINT AUTO_INCREMENT, name LONGTEXT, description LONGTEXT, emergency TINYINT(1) DEFAULT '0', latitude DOUBLE(18, 8), longitude DOUBLE(18, 8), PRIMARY KEY(id)) DEFAULT CHARACTER SET utf8 COLLATE utf8_unicode_ci ENGINE = INNODB;
CREATE TABLE users (id BIGINT AUTO_INCREMENT, name LONGTEXT, surname LONGTEXT, avatar LONGTEXT, created_at DATETIME NOT NULL, updated_at DATETIME NOT NULL, PRIMARY KEY(id)) DEFAULT CHARACTER SET utf8 COLLATE utf8_unicode_ci ENGINE = INNODB;
